import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from '../../environments/environment';

@Component({
  selector: 'app-kinetic-energy-calculator',
  templateUrl: './kinetic-energy-calculator.component.html',
  styleUrls: ['./kinetic-energy-calculator.component.css']
})
export class KineticEnergyCalculatorComponent {
  public flyingObject: FlyingObject = {
    mass: 0,
    velocity: 0
  };
  public kineticEnergy: number = 0;
  baseApiUrl: string = environment.baseApiUrl;

  constructor(private http: HttpClient) { }

  public calculateKineticEnergy() {
    console.log(this.flyingObject);
    this.http.get(this.baseApiUrl + 'kineticenergycalculator').subscribe(result => {
      this.kineticEnergy = Number(result);
      console.log(this.kineticEnergy);
    }, error => console.error(error));
  }
}

interface FlyingObject {
  mass: number;
  velocity: number;
}
